<div class="right_col" role="main">     <div class="row">          <div class="col-md-12 col-sm-12 col-xs-12">               <div class="x_panel">                    <div class="x_title">                         <h2>Voxbay missed calls</h2>                         <div class="clearfix"></div>                    </div>                    <div class="x_content">                         <table id="voxbayAllCallListPending" class="table table-striped table-bordered">                              <thead>                                   <tr>                                        <th>Customer number</th>                                        <th>To reg</th>                                   </tr>                              </thead>                         </table>                    </div>               </div>          </div>     </div></div><script>     $(document).ready(function () {          $('#voxbayAllCallListPending').dataTable({               "processing": true,               "serverSide": true,               'serverMethod': 'post',               "ajax": site_url + "voxbay/pendingCallsAjax",               'initComplete': function (settings) {},               'columns': [                    {                         sortable: false,                         "render": function (data, type, full, meta) {                              var url = "https://api.whatsapp.com/send?phone=" + full.ccb_callerNumber;                              return "<a href='" + url + "'>" + full.ccb_callerNumber + "</a>";                         }                    },                    {                         sortable: false,                         "render": function (data, type, full, meta) {                              var url = site_url + "registration/add/" + full.ccb_id + '/2';                              return "<a href='" + url + "'> <i class='fa fa-arrow-right'></i>";                         }                    }               ]          });     });</script>